import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Papa from 'papaparse';

const GoogleSheet = () => {
  const [data, setData] = useState([]);
  const [selectedId, setSelectedId] = useState('');
  const [newRate, setNewRate] = useState('');
  const [email] = useState('santiago.valdes99@gmail.com');

  const SPREADSHEET_ID = '2PACX-1vSemz3pLNNHX48BFdqar2K1X-6bhC5T4H8zl1PkzMi5luHafj3Sv-2wqSdkKTr7O_NjWPCP5S1owVpB';
  // const SHEET_NAME = 'Sheet1'; // Adjust the sheet name if necessary
  const CSV_URL = `https://docs.google.com/spreadsheets/d/e/${SPREADSHEET_ID}/pub?output=csv`;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(CSV_URL);
        const csv = response.data;
        Papa.parse(csv, {
          header: true,
          complete: (results) => {
            const rows = results.data.map((row) => ({
              idOp: row.idOp,
              tasa: row.tasa,
              email: row.email
            }));
            setData(rows);
          }
        });
      } catch (error) {
        console.error('Error fetching data:', error);
        alert('Failed to fetch data. Please try again later.');
      }
    };
    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const postData = {
    // Que el id sea un número
      idOp: parseInt(selectedId),
      tasa: parseFloat(newRate),
      email: email
    };
    try {
      // Mostrar el body del post en un alert
      alert(JSON.stringify(postData));
      // Hacer un post a la URL de Zapier (https://hooks.zapier.com/hooks/catch/6872019/oahrt5g/) con el body postData
      await axios.post('https://hooks.zapier.com/hooks/catch/6872019/oahrt5g/', postData);
      alert('Request sent successfully!');
    } catch (error) {
      // Ver el error en una aleta y mostrar un alert con un mensaje de error
      alert(error);
      alert('Failed to send request. Please try again later.');
    }
  };

  return (
    <div>
      <h2>Google Sheet Embed</h2>
      <iframe
        src={`https://docs.google.com/spreadsheets/d/e/${SPREADSHEET_ID}/pubhtml`}
        width="600"
        height="400"
        title="Google Sheets"
      ></iframe>
      <h3>Edit Rate</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Select ID:
          <select value={selectedId} onChange={(e) => setSelectedId(e.target.value)}>
            <option value="">Select ID</option>
            {data.map((row) => (
              <option key={row.idOp} value={row.idOp}>
                {row.idOp}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          New Rate:
          <input
            type="number"
            step="0.01"
            value={newRate}
            onChange={(e) => setNewRate(e.target.value)}
            required
          />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default GoogleSheet;
