import React, { useState, useEffect } from 'react';
import Papa from 'papaparse';
import axios from 'axios';

const GoogleSheet = () => {
  const [data, setData] = useState([]);
  const [selectedId, setSelectedId] = useState('');
  const [newRate, setNewRate] = useState('');

  const SPREADSHEET_ID = '2PACX-1vSemz3pLNNHX48BFdqar2K1X-6bhC5T4H8zl1PkzMi5luHafj3Sv-2wqSdkKTr7O_NjWPCP5S1owVpB';
  const CSV_URL = `https://docs.google.com/spreadsheets/d/e/${SPREADSHEET_ID}/pub?output=csv`;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(CSV_URL);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const csv = await response.text();
        Papa.parse(csv, {
          header: true,
          complete: (results) => {
            const rows = results.data.map((row) => ({
              idOp: row.idOp,
              tasa: row.tasa,
              email: row.email
            }));
            setData(rows);
          }
        });
      } catch (error) {
        console.error('Error fetching data:', error);
        alert('Failed to fetch data. Please try again later.');
      }
    };
    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Obtener el correo electrónico asociado al ID seleccionado
    const selectedRow = data.find(row => row.idOp === selectedId);
    if (!selectedRow) {
      alert('Selected ID not found.');
      return;
    }
    const userEmail = selectedRow.email;

    // Actualizar el valor en Google Sheets
    const updatedData = [...data];
    const indexToUpdate = updatedData.findIndex(row => row.idOp === selectedId);
    if (indexToUpdate !== -1) {
      updatedData[indexToUpdate].tasa = newRate.toString(); // Actualizar el valor de tasa
      setData(updatedData);
    } else {
      alert('Error updating data.');
      return;
    }

    // Enviar solicitud al webhook y actualizar en Google Sheets
    const postData = {
      idOp: selectedId,
      tasa: parseFloat(newRate),
      email: userEmail
    };
    try {
      await axios.post('https://hooks.zapier.com/hooks/catch/6872019/oahrt5g/', postData);
      await axios.post('https://script.google.com/macros/s/AKfycbxB6bhC5T4H8zl1PkzMi5luHafj3Sv-2wqSdkKTr7O/exec', postData);
      alert('Request sent successfully!');
    } catch (error) {
      console.error('Error sending request', error);
      alert('Failed to send request, please try again.');
    }
  };

  return (
    <div>
      <h2>Google Sheet Embed</h2>
      <iframe
        src={`https://docs.google.com/spreadsheets/d/e/${SPREADSHEET_ID}/pubhtml`}
        width="600"
        height="400"
        title="Google Sheets"
      ></iframe>
      <h3>Edit Rate</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Select ID:
          <select value={selectedId} onChange={(e) => setSelectedId(e.target.value)}>
            <option value="">Select ID</option>
            {data.map((row) => (
              <option key={row.idOp} value={row.idOp}>
                {row.idOp}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          New Rate:
          <input
            type="number"
            step="0.01"
            value={newRate}
            onChange={(e) => setNewRate(e.target.value)}
            required
          />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default GoogleSheet;
