import React, { useState, useEffect } from 'react';
import axios from 'axios';

const GoogleSheet = () => {
  const [data, setData] = useState([]);
  const [selectedId, setSelectedId] = useState('');
  const [newRate, setNewRate] = useState('');
  const [email, setEmail] = useState('santiago.valdes99@gmail.com');

  // https://docs.google.com/spreadsheets/d/e/2PACX-1vSemz3pLNNHX48BFdqar2K1X-6bhC5T4H8zl1PkzMi5luHafj3Sv-2wqSdkKTr7O_NjWPCP5S1owVpB/pubhtml
  useEffect(() => {
    const fetchData = async () => {
      const result = await axios.get('https://spreadsheets.google.com/feeds/list/YOUR_SPREADSHEET_ID/od6/public/values?alt=json');
      const rows = result.data.feed.entry.map((entry) => ({
        idOp: entry.gsx$idop.$t,
        tasa: entry.gsx$tasa.$t,
        email: entry.gsx$email.$t
      }));
      setData(rows);
    };
    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const postData = {
      idOp: selectedId,
      tasa: parseFloat(newRate),
      email: email
    };
    try {
      await axios.post('https://hooks.zapier.com/hooks/catch/6872019/oahrt5g/', postData);
      alert('Request sent successfully!');
    } catch (error) {
      console.error('Error sending request', error);
      alert('Failed to send request.');
    }
  };

  return (
    <div>
      <h2>Google Sheet Embed</h2>
      <iframe
        src="https://docs.google.com/spreadsheets/d/e/2PACX-1vYOUR_SPREADSHEET_ID/pubhtml?widget=true&headers=false"
        width="600"
        height="400"
        title="Google Sheets"
      ></iframe>
      <h3>Edit Rate</h3>
      <form onSubmit={handleSubmit}>
        <label>
          Select ID:
          <select value={selectedId} onChange={(e) => setSelectedId(e.target.value)}>
            <option value="">Select an ID</option>
            {data.map((row) => (
              <option key={row.idOp} value={row.idOp}>
                {row.idOp}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          New Rate:
          <input
            type="number"
            step="0.01"
            value={newRate}
            onChange={(e) => setNewRate(e.target.value)}
            required
          />
        </label>
        <br />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default GoogleSheet;
